import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import previous from '../images/previous.png';

const states = ['Rio de Janeiro',
  'Minas Gerais', 'Amapá', 'Amazonas', 'São Paulo', 'Ceará', 'Distrito Federal'];

class PayForms extends Component {
  constructor(props) {
    super(props);
    this.state = {
      fullname: '',
      email: '',
      cep: '',
      phone: '',
      address: '',
      complement: '',
      number: '',
      city: '',
      countryState: '',
      boleto: '',
      credit: '',
    };
  }

  handleChange({ target }) {
    const { name } = target;
    const value = target.type === 'checkbox' ? target.checked : target.value;
    console.log(value);

    this.setState({
      [name]: value,
    });
  }

  handleSubmit(event) {
    event.preventDefault();
  }

  inputText(name, type, data, holder) {
    return (
      <label htmlFor={ name }>
        <input
          type={ type }
          name={ name }
          data-testid={ data }
          placeholder={ holder }
          onChange={ this.handleChange.bind(this) }
          required
        />
      </label>
    );
  }

  render() {
    return (
      <>
        <Link to="/">
          <img src={ previous } alt="Imagem de uma seta" className="previous-shop" />
        </Link>
        <form>
          <fieldset>
            <legend>Informações do Comprador</legend>
            { this.inputText('Name', 'text', 'checkout-fullname', 'Nome Completo') }
            { this.inputText('email', 'email', 'checkout-email', 'Email@.com') }
            { this.inputText('cep', 'text', 'checkout-cep', 'CEP') }
            { this.inputText('phone', 'text', 'checkout-phone', 'Telefone') }
            { this.inputText('address', 'text', 'checkout-address', 'Endereço') }
            { this.inputText('complement', 'text', 'checkout-complement', 'Complemento') }
            { this.inputText('number', 'text', 'checkout-number', 'Número') }
            { this.inputText('city', 'text', 'checkout-city', 'Cidade') }
            <select
              name="countryState"
              required
              onChange={ this.handleChange.bind(this) }
              defaultValue=""
            >
              <option value="">Selecione</option>
              {
                states.map((value, key) => (
                  <option key={ key }>{ value }</option>
                ))
              }
            </select>
            <input
              type="button"
              value="Comprar"
              data-testid="checkout-products"
              onClick={ this.handleSubmit.bind(this) }
            />
          </fieldset>
          <fieldset>
            <legend>Método de Pagamento</legend>
            <label htmlFor="boleto">
              <p>Boleto</p>
              <input
                type="checkbox"
                name="boleto"
                onChange={ this.handleChange.bind(this) }
              />
            </label>
          </fieldset>
        </form>
      </>
    );
  }
}

export default PayForms;
