import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import previous from '../images/previous.png';

const states = ['Rio de Janeiro', 'Minas Gerais', 'Amapá', 'Amazonas', 'São Paulo', 'Ceará', 'Distrito Federal'];

class PayForms extends Component {
  constructor(props) {
    super(props);
    this.state = {
      value: '',
      fullname: '',
      email: '',
      cep: '',
      phone: '',
      address: '',
    };
  }

  handleChange() {

  }

  inputForm(name, type, data, holder) {
    return (
      <label htmlFor={ name }>
        <input type={ type } name={ name } data-testid={ data } placeholder={ holder } />
      </label>
    );
  }

  render() {
    return (
      <>
        <Link to="/">
          <img src={ previous } alt="Imagem de uma seta" className="previous-shop" />
        </Link>
        <form>
          <fieldset>
            <legend>Informações do Comprador</legend>
            { this.inputForm('Name', 'text', 'checkout-fullname', 'Nome Completo') }
            { this.inputForm('email', 'email', 'checkout-email', 'Email@.com') }
            { this.inputForm('cep', 'number', 'checkout-cep', 'CEP') }
            { this.inputForm('phone', 'number', 'checkout-phone', 'Telefone') }
            { this.inputForm('address', 'text', 'checkout-address', 'Endereço') }
            { this.inputForm('complement', 'text', 'checkout-complement', 'Complemento') }
            { this.inputForm('number', 'number', 'checkout-number', 'Número') }
            { this.inputForm('city', 'text', 'checkout-city', 'Cidade') }
              Estado:
              <select
              name="countryState"
              required
              onChange={ changeHandler }
              defaultValue=""
            >
              <option value="">Selecione</option>
              {
                states.map((value, key) => (
                  <option key={ key }>{ value }</option>
                ))
              }
              <input type="button" value="Comprar" data-testid="checkout-products" />
            </select>
          </fieldset>
        </form>
      </>
    );
  }
}

export default PayForms;
