import React, { Component } from 'react';

class PayForms extends Component {
  constructor(props) {
    super(props);
    this.state = {
      fullname: '',
      email: '',
      cep: '',
      phone: '',
      address: '',
    };
  }

  inputForm(name, type, data, holder) {
    return <label htmlFor={ name }>
      { name }
      <input type={ type } name={ name } data-testid={ data } placeholder={ holder } />
    </label>;
  }

  render() {
    return (
      <form>
        <fieldset>
          <legend>Informações do Comprador</legend>
          <label htmlFor="fullname">
            Nome completo:
            <input type="text" name="fullname" data-testid="checkout-fullname" placeholder="Nome" />
          </label>
          <label htmlFor="email">
            Email:
            <input type="email" name="email" data-testid="checkout-email" placeholder="Email@.com" />
          </label>
          <label htmlFor="cep">
            CEP:
            <input type="number" name="cep" data-testid="checkout-cep" placeholder="CEP" />
          </label>
          <label htmlFor="phone">
            Telefone:
            <input type="number" name="phone" data-testid="checkout-phone" placeholder="Telefone" />
          </label>
          <label htmlFor="address">
            Endereço:
            <input type="text" name="address" data-testid="checkout-address" placeholder="Endereço" />
          </label>
          <input type="button" value="Finalizar compra" data-testid="checkout-products" />
        </fieldset>
      </form>
    );
  }
}

export default PayForms;
