import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import previous from '../images/previous.png';

class PayForms extends Component {
  constructor(props) {
    super(props);
    this.state = {
      fullname: '',
      email: '',
      cep: '',
      phone: '',
      address: '',
    };
  }

  inputForm(name, type, data, holder) {
    return (
      <label htmlFor={ name }>
        <input type={ type } name={ name } data-testid={ data } placeholder={ holder } />
      </label>
    );
  }

  render() {
    return (
      <>
        <Link to="/">
          <img src={ previous } alt="Imagem de uma seta" className="previous-shop" />
        </Link>
        <form>
          <fieldset>
            <legend>Informações do Comprador</legend>
            { this.inputForm('Name', 'text', 'checkout-fullname', 'Nome Completo') }
            { this.inputForm('email', 'email', 'checkout-email', 'Email@.com') }
            { this.inputForm('cep', 'number', 'checkout-cep', 'CEP') }
            { this.inputForm('phone', 'number', 'checkout-phone', 'Telefone') }
            { this.inputForm('cep', 'number', 'checkout-cep', 'CEP') }
            { this.inputForm('cep', 'number', 'checkout-cep', 'CEP') }


            <label htmlFor="phone">
              Telefone:
              <input type="number" name="phone" data-testid="checkout-phone" placeholder="Telefone" />
            </label>
            <label htmlFor="address">
              Endereço:
              <input type="text" name="address" data-testid="checkout-address" placeholder="Endereço" />
            </label>
            <input type="button" value="Comprar" data-testid="checkout-products" />
          </fieldset>
        </form>
      </>
    );
  }
}

export default PayForms;
