import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import previous from '../images/previous.png';

const states = ['Rio de Janeiro',
  'Minas Gerais', 'Amapá', 'Amazonas', 'São Paulo', 'Ceará', 'Distrito Federal'];

class PayForms extends Component {
  constructor(props) {
    super(props);
    this.state = {
      fullname: '',
      email: '',
      cep: '',
      phone: '',
      address: '',
      complement: '',
      number: '',
      city: '',
      countryState: '',
    };
  }

  handleChange({ target }) {
    const { name, value } = target;
    value = target.type === 'checkbox' ? target.checked : target.value;

    this.setState({
      [name]: value,
    });
  }

  handleSubmit(event) {
    event.preventDefault();
  }

  // validateField(fieldName, value) {
  //   switch (fieldName) {
  //     case 'email':
  //       const isValid = value
  //         .match(/^([\w.%+-]+)@([\w-]+\.)+([\w]{2})$/i);
  //       return isValid ? '' : ' is invalid';
  //     default:
  //       break;
  //   }
  //   return '';
  // }

  inputForm(name, type, data, holder) {
    return (
      <label htmlFor={ name }>
        <input
          type={ type }
          name={ name }
          data-testid={ data }
          placeholder={ holder }
          onChange={ this.handleChange.bind(this) }
          required
        />
      </label>
    );
  }

  render() {
    return (
      <>
        <Link to="/">
          <img src={ previous } alt="Imagem de uma seta" className="previous-shop" />
        </Link>
        <form>
          <fieldset>
            <legend>Informações do Comprador</legend>
            { this.inputForm('Name', 'text', 'checkout-fullname', 'Nome Completo') }
            { this.inputForm('email', 'email', 'checkout-email', 'Email@.com') }
            { this.inputForm('cep', 'text', 'checkout-cep', 'CEP') }
            { this.inputForm('phone', 'text', 'checkout-phone', 'Telefone') }
            { this.inputForm('address', 'text', 'checkout-address', 'Endereço') }
            { this.inputForm('complement', 'text', 'checkout-complement', 'Complemento') }
            { this.inputForm('number', 'text', 'checkout-number', 'Número') }
            { this.inputForm('city', 'text', 'checkout-city', 'Cidade') }
            <select
              name="countryState"
              required
              onChange={ this.handleChange.bind(this) }
              defaultValue=""
            >
              <option value="">Selecione</option>
              { states.map((value, key) => (
                <option key={ key }>{ value }</option>
              )) }
            </select>
            <input
              type="button"
              value="Comprar"
              data-testid="checkout-products"
              onClick={ this.handleSubmit.bind(this) }
            />
          </fieldset>
        </form>
      </>
    );
  }
}

export default PayForms;
